import React, { useState, useRef } from "react";

export default function Post() {
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [selectedImageDataUrl, setSelectedImageDataUrl] = useState(null);
  const fileInputRef = useRef(null);
  const posterRef = useRef(null);
  
  

  // âœ… Default values
  const defaultImage =
    "data:image/jpeg;base64,";
  const defaultName = "John Doe";
  const defaultAge = "25";

  const handleImageChange = (e) => {
    const file = e.target.files && e.target.files[0];
    if (!file) {
      setSelectedImageDataUrl(null);
      return;
    }
    if (!file.type.startsWith("image/")) {
      alert("Please upload an image file.");
      e.target.value = "";
      return;
    }
    const reader = new FileReader();
    reader.onload = (ev) => {
      setSelectedImageDataUrl(ev.target.result);
    };
    reader.readAsDataURL(file);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!name.trim() || !age || !selectedImageDataUrl) {
      alert("Please fill in all fields and upload an image.");
      return;
    }
  };

  const handleDownload = () => {
    const canvas = document.createElement("canvas");
    canvas.width = 500;
    canvas.height = 700;
    const ctx = canvas.getContext("2d");

    const img = new Image();
    img.crossOrigin = "anonymous"; // for placeholder
    img.onload = () => {
      ctx.drawImage(img, 0, 0, 500, 700);
      ctx.fillStyle = "rgba(0,0,0,0.6)";
      ctx.fillRect(0, 640, 500, 60);

      ctx.fillStyle = "white";
      ctx.font = "bold 28px Arial";
      ctx.textAlign = "center";
      ctx.fillText(`${name || defaultName} â€” Age ${age || defaultAge}`, 250, 680);

      const link = document.createElement("a");
      link.download = "poster.png";
      link.href = canvas.toDataURL("image/png");
      link.click();
    };
    img.src = selectedImageDataUrl || defaultImage;
  };

  const handleReset = () => {
    setName("");
    setAge("");
    setSelectedImageDataUrl(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = "";
    }
  };

  return (
    <div className="wrapper" style={styles.wrapper}>
      <form style={styles.card} onSubmit={handleSubmit} autoComplete="off">
        <h2>Create Your Poster</h2>

        <div style={styles.row}>
          <label>Image (upload)</label>
          <input
            type="file"
            accept="image/*"
            onChange={handleImageChange}
            ref={fileInputRef}
          />
        </div>

        <div style={styles.row}>
          <label>Name</label>
          <input
            type="text"
            placeholder="Enter your name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </div>

        <div style={styles.row}>
          <label>Age</label>
          <select value={age} onChange={(e) => setAge(e.target.value)}>
            <option value="">Select age</option>
            {Array.from({ length: 100 }, (_, i) => (
              <option key={i + 1} value={i + 1}>
                {i + 1}
              </option>
            ))}
          </select>
        </div>
      </form>

      {/* âœ… Always show example poster */}
      <div style={{ display: "block" }}>
        <div style={styles.poster} ref={posterRef}>
          <img
            src={selectedImageDataUrl || defaultImage}
            alt="Poster"
            style={{ width: "100%", display: "block" }}
          />
          <div style={styles.posterText}>
            {name || defaultName} â€” Age {age || defaultAge}
          </div>
        </div>
      </div>

      <button onClick={handleDownload}>Download PNG</button>
    </div>
  );
}

const styles = {
  wrapper: {
    fontFamily: "system-ui, sans-serif",
    background: "linear-gradient(180deg,#071029 0%, #071a2a 100%)",
    minHeight: "100vh",
    padding: "20px",
    color: "#e6eef6",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    gap: "20px",
  },
  card: {
    background:
      "linear-gradient(180deg, rgba(255,255,255,0.03), rgba(255,255,255,0.01))",
    borderRadius: "12px",
    padding: "20px",
    width: "100%",
    maxWidth: "420px",
    boxShadow: "0 8px 30px rgba(2,6,23,0.6)",
    border: "1px solid rgba(255,255,255,0.04)",
  },
  row: {
    marginBottom: "14px",
  },
  poster: {
    position: "relative",
    width: "300px",
    borderRadius: "12px",
    overflow: "hidden",
    boxShadow: "0 8px 20px rgba(0,0,0,0.6)",
    textAlign: "center",
  },
  posterText: {
    position: "absolute",
    bottom: 0,
    width: "100%",
    background: "linear-gradient(to top, rgba(0,0,0,0.8), transparent)",
    color: "white",
    padding: "12px",
    fontSize: "18px",
    fontWeight: "bold",
  },
};
